PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE [feeds] (
   [id] TEXT PRIMARY KEY,
   [title] TEXT,
   [subtitle] TEXT,
   [link] TEXT,
   [author] TEXT,
   [updated] TEXT
);
CREATE TABLE [links] (
   [id] TEXT PRIMARY KEY,
   [feed] TEXT REFERENCES [feeds]([id]),
   [title] TEXT,
   [description] TEXT,
   [published] TEXT,
   [updated] TEXT,
   [link] TEXT
, [og] TEXT);
CREATE TABLE [mastodon] (
   [link_id] TEXT REFERENCES [links]([id]),
   [posted] TEXT,
   [status] TEXT,
   [post_url] TEXT,
   [error] TEXT
);
COMMIT;
